import json#读取json文件text  = open('Info1.json')info = json.load(text)def get_list_3(info):    # Store info of list 3:    Info = []    for i in range(len(info)):        for j in range(len(info[i]['stations'])-1):            a = []            # start_station            a.append(info[i]['stations'][j]['name'])            # next_station            a.append(info[i]['stations'][j+1]['name'])            # 权重            weight = 1            a.append(weight)            # line            a.append(info[i]['name'])            #print(a)            Info.append(a)    return Info#Info = get_list_3(info)#print(Info)