import json# 读取json文件text = open('Info1.json')info = json.load(text)#print(info)def get_list_2(info):    Info = []    for i in range(len(info)):        a = []        #start_id:        a.append(info[i]['stations'][0]['name'])        # end_id:        a.append(info[i]['stations'][len(info[i]['stations'])-1]['name'])        #wait_time:        a.append(info[i]['headway'])        #work_endtime:        a.append(info[i]['endTime'])        #line_id:        a.append(info[i]['name'])        #print(a)        Info.append(a)    #print(Info)    return InfoInfo = get_list_2(info)#print(Info)