import jsonimport conversion_changee# 读取json文件text = open('Info1.json')info = json.load(text)#print(info[0])def get_list_1(info):    Info = []    for i in range(len(info)):        # 线路uid        name = info[i]['name']        #第j个途径站        for j in range(len(info[i]['stations'])):            a = []            # station name:            a.append(info[i]['stations'][j]['name'])            try:                station = {}                station[info[i]['stations'][j+1]['name']] = name                a.append(station)            except:                a.append(None)            lon = info[i]['stations'][j]['geo'][2:13]            lat = info[i]['stations'][j]['geo'][14:24]            lon,lat = conversion_changee.get_lonlat(float(lon),float(lat))            a.append(lon)            a.append(lat)            Info.append(a)    return Info#Info = get_list_1(info)#print(Info)