import jsonimport conversion"""# 读取json文件text = open('Info1.json')info = json.load(text)print(info)"""#站点信息def get_list_1(info):    Info = []    for i in range(len(info)):        # 线路uid        name = info[i]['name']        #第j个途径站        for j in range(len(info[i]['stations'])):            a = []            # station name:            a.append(info[i]['stations'][j]['name'])            try:                station = {}                station[info[i]['stations'][j+1]['name']] = name                a.append(station)            except:                a.append(None)            lon = info[i]['stations'][j]['geo'][2:13]            lat = info[i]['stations'][j]['geo'][14:24]            lon,lat = conversion_changee.get_lonlat(float(lon),float(lat))            a.append(lon)            a.append(lat)            Info.append(a)    return Info#线路信息def get_list_2(info):    Info = []    for i in range(len(info)):        a = []        #start_id:        a.append(info[i]['stations'][0]['name'])        # end_id:        a.append(info[i]['stations'][len(info[i]['stations'])-1]['name'])        #wait_time:        a.append(info[i]['headway'])        #work_endtime:        a.append(info[i]['endTime'])        #line_id:        a.append(info[i]['name'])        #print(a)        Info.append(a)    #print(Info)    return Info#两个站点间信息def get_list_3(info):    # Store info of list 3:    Info = []    for i in range(len(info)):        for j in range(len(info[i]['stations'])-1):            a = []            # start_station            a.append(info[i]['stations'][j]['name'])            # next_station            a.append(info[i]['stations'][j+1]['name'])            # 权重            weight = 1            a.append(weight)            # line            a.append(info[i]['name'])            #print(a)            Info.append(a)    return Info